
public class TypeBoolean {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 논리형 
		 true와 false 중 하나를 저장할 수 있으며
		 기본값(default)은 false이다.
		 
		 */
		
		
		boolean power = true;
		
		System.out.println(power);
		
		
		power = false;
		
		System.out.println(power);
		System.out.println(false);
	}

}
