
public class TypeChar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		  문자형 char
		 char 타입
		 단 하나의 문자 저장
		 */
		
		
		char ch = 'A';
		char sh = 'B';
		
		System.out.println(ch);
		
		System.out.println(sh);
		
		System.out.println(ch + 1);
		
		System.out.println(ch + sh);
		
		/*
		ch = ''; // 한글자 용
		string str = ""; // 한글자 이상용
		문자
		문자열
		
		*/
		/*
		 특수문자
		 */
		System.out.print("\t");
		
		System.out.print("123\t");
		// 탭기능
		System.out.print("\t123");
		// 탭기능
		System.out.print("\n");
		// 줄바꿈
		System.out.print("\"");
		// \을 사용하면 특수문자 사용 가능
		System.out.print("\"이젠 아카데미\"");
		
	}

}
