
public class Ch3OperatorEx5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 
		 비교 연산자
		 크다, 작다, 같다, 다르다 같은 것
		  
		  
		  
		  */
		  
		int x = 10;
		int y = 3;
		
		
		
		System.out.println(x == y); // 동등 연산자
		System.out.println(x != y); // 부정 연산자
		System.out.println(x > y);
		System.out.println(x < y);
		System.out.println(x >= y);
		
		
		
		
	}

}
