
public class Ch4FlowEx13 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		  반복문
		 
		while문 와일문 
		 
		 

		while(조건식) {
			조건식의 연산결과가 true 인 동안, 반복될 문장들을 적는다
		
		 }
		 */
		

		int cnt = 5;
		
		while(cnt-- != 0) { 
			System.out.println(cnt + " I can Do it");
			
		}
	}
}
