
public class Ch4_2RefeatTest1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//      1부터 10까지의 합을 구하시오
	      
//      1
//      3
//      6
//      10
//      ,
//      ,
//      ,
//      55
		
		int num = 0;
		
		for (int i = 0; i <= 10; ++i) {
			
			num = num + i;
			
			System.out.println(num);
		}
		System.out.println("num의 값은 " + num + "입니다.");
	}

}
