
public class Ch4_2RefeatTest12 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 
		 
		 
		 */

		for (int i = 0; i < 4; i++) {
			for (int j = 2; j >= i; j--) {
				
				System.out.print(" ");
				
			}
			for (int j = 0; j <= i; j++) {
				System.out.print("*");
			}
		System.out.println();
		}
		
	}
}	

		



