package two;

public class StringEx6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		   //절대 원본 데이터를 변경하지 않는다
	      String originStr = "Hello.java"; //기준
	      String fileName = ""; //결과
	      String fileExtensionName = ""; // 파일 확장자명
	      
//	      	파일명과 파일확장자를 출력하시오
//	        ex:
//	    	  	파일전체명: Hello.java
//	    	  	파일명: Hello
//	    	  	확장자명: java
	      
	      //substring 사용
	      fileName = originStr.substring(0, originStr.indexOf('.'));
	      fileExtensionName = originStr.substring(originStr.indexOf('.') + 1);
	      
	      //split 사용
//	      String[] arr = originStr.split("[.]");
	     
	      
//	      fileName = arr[0]; 
//	      fileExtensionName = arr[1];
	      
	      // ---- 출력결과는 건드리지 않는다. ----
	      System.out.println(fileName);
	      System.out.println(fileExtensionName);

	}

}
